import{_ as a,c as o,o as i,ae as d}from"./chunks/framework.DI6M9D4h.js";const u=JSON.parse('{"title":"Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"guide/changelog.md","filePath":"guide/changelog.md"}'),t={name:"guide/changelog.md"};function l(n,e,r,c,s,h){return i(),o("div",null,[...e[0]||(e[0]=[d('<h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-label="Permalink to &quot;Changelog&quot;">​</a></h1><p>All notable changes to this project will be documented in this file.</p><h2 id="_3-0-0-alpha-4-2025-12-26" tabindex="-1">[3.0.0-alpha.4] - 2025-12-26 <a class="header-anchor" href="#_3-0-0-alpha-4-2025-12-26" aria-label="Permalink to &quot;[3.0.0-alpha.4] - 2025-12-26&quot;">​</a></h2><h3 id="added" tabindex="-1">Added <a class="header-anchor" href="#added" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li><strong>Dynamic Theming Engine</strong>: Support for multiple color schemes (<code>Quantum</code>, <code>Nebula</code>, <code>Aurora</code>) via CSS variables.</li><li><strong>PeacockAccordion</strong>: A motion-first accordion component with <code>glass</code>, <code>outline</code>, and <code>ghost</code> variants.</li><li><strong>PeacockDropdownMenu</strong>: A high-fidelity, glassmorphic dropdown system built on Radix UI primitives.</li><li>Added <code>scheme</code> prop to <code>PeacockProvider</code> for global theme switching.</li></ul><h3 id="changed" tabindex="-1">Changed <a class="header-anchor" href="#changed" aria-label="Permalink to &quot;Changed&quot;">​</a></h3><ul><li>Migrated core color system to CSS variables to support runtime theming.</li><li>Updated documentation with interactive theme switcher demo.</li></ul><h2 id="_3-0-0-alpha-3-2025-12-26" tabindex="-1">[3.0.0-alpha.3] - 2025-12-26 <a class="header-anchor" href="#_3-0-0-alpha-3-2025-12-26" aria-label="Permalink to &quot;[3.0.0-alpha.3] - 2025-12-26&quot;">​</a></h2><h3 id="added-1" tabindex="-1">Added <a class="header-anchor" href="#added-1" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li>Added `layoutId` prop to <strong>PeacockTabs</strong> to allow scoped shared-layout animations.</li><li>Added `CommandSeparator` export to <strong>PeacockCommand</strong>.</li></ul><h3 id="fixed" tabindex="-1">Fixed <a class="header-anchor" href="#fixed" aria-label="Permalink to &quot;Fixed&quot;">​</a></h3><ul><li>Fixed motion conflicts when multiple <strong>PeacockTabs</strong> instances are present on the same page.</li><li>Improved React-Vue bridge stability for complex dashboard layouts.</li></ul><h2 id="_3-0-0-alpha-2-2025-12-26" tabindex="-1">[3.0.0-alpha.2] - 2025-12-26 <a class="header-anchor" href="#_3-0-0-alpha-2-2025-12-26" aria-label="Permalink to &quot;[3.0.0-alpha.2] - 2025-12-26&quot;">​</a></h2><h3 id="added-2" tabindex="-1">Added <a class="header-anchor" href="#added-2" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li>New <strong>PeacockTabs</strong> component with <code>pill</code>, <code>glass</code>, and <code>underline</code> variants.</li><li><strong>ComponentPreview</strong> system for high-fidelity documentation.</li><li>Perspective utility system in Tailwind plugin (<code>perspective-500</code>, <code>perspective-1000</code>).</li><li>Interactive <strong>HeroDemos</strong> on the landing page.</li></ul><h3 id="fixed-1" tabindex="-1">Fixed <a class="header-anchor" href="#fixed-1" aria-label="Permalink to &quot;Fixed&quot;">​</a></h3><ul><li>Fixed <code>PeacockDock</code> mouse tracking by switching from <code>pageX</code> to <code>clientX</code>.</li><li>Corrected versioning in documentation navigation.</li><li>Standardized demo container styling.</li></ul><h2 id="_3-0-0-alpha-1-2025-12-25" tabindex="-1">[3.0.0-alpha.1] - 2025-12-25 <a class="header-anchor" href="#_3-0-0-alpha-1-2025-12-25" aria-label="Permalink to &quot;[3.0.0-alpha.1] - 2025-12-25&quot;">​</a></h2><h3 id="added-3" tabindex="-1">Added <a class="header-anchor" href="#added-3" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li>Initial release of the <strong>Quantum Engine</strong>.</li><li>Core components: <code>HolographicCard</code>, <code>PeacockDock</code>, <code>PeacockButton</code>, <code>FluidCard</code>, <code>PeacockInput</code>.</li><li>Plumage Engine v2 (Tailwind Plugin).</li><li>Dark-mode first design system.</li><li>Physics-based motion integration with Framer Motion 12.</li></ul>',20)])])}const m=a(t,[["render",l]]);export{u as __pageData,m as default};
